sys_variable_value.value:
(function execute(inputs, outputs) {

// Send Command:

  var cmd = inputs.command.trim();
  var exec_timeout = inputs.exec_timeout;

  if( isNaN(exec_timeout) || exec_timeout <= 0 ) exec_timeout = 15; 

  var ecc_queue = new GlideRecord("ecc_queue");
  ecc_queue.initialize();
  ecc_queue.agent = "mid.server." + inputs.midserver;
  ecc_queue.queue = "output";
  ecc_queue.topic = "Command";
  if ( cmd.length > 100 ) {
  	ecc_queue.name = cmd.slice(0,10) + ' [.. long command ..]';
    ecc_queue.payload = '<?xml version="1.0" encoding="UTF-8"?><parameters><parameter name="skip_sensor" value="true"/><parameter name="name" value="' + cmd.replace( /\&/g, '&amp;').replace( /\"/g, '&quot;').replace( /\'/g, '&apos;').replace( /\</g, '&lt;').replace( /\>/g, '&gt;') + '"/></parameters>';
  }
  else { 
  	ecc_queue.name = cmd;
    ecc_queue.payload = '<?xml version="1.0" encoding="UTF-8"?><parameters><parameter name="skip_sensor" value="true"/></parameters>';
  }

  //Get SysID of new ECC Queue record
  var ecc_sysid = ecc_queue.insert();


  if( inputs.async_run == 'false' ) {
       //Get Response from ECC Queue that was linked to the original Output command in the response_to reference 
         var response_sysid = '-';
         var count = 0; 
         var ecc_response = new GlideRecord("ecc_queue");

         //Get response that matches response to record set in output command sent earlier

         while ( (response_sysid == '-') && (count < (exec_timeout * 1000) ) ) {
           ecc_response.addQuery("response_to", ecc_sysid);
           ecc_response.addQuery("queue", "input");
           ecc_response.query();
           if (ecc_response.next()) {
                response_sysid = ecc_response.sys_id;
           } 
           else {
                response_sysid = '-';
                count+=500;
                gs.sleep(500);
           }
         }

         if (response_sysid != '-') {
           	  outputs.result = 'Command terminated in ' + ((count-500)/1000) + ' seconds.';
              var xmldoc = new XMLDocument(ecc_response.payload);
			  outputs.stdout = xmldoc.getNodeText('//stdout');
              outputs.stderr = xmldoc.getNodeText('//stderr');
         } 
         else {
              outputs.result = 'Timeout getting response in ' + ((count-500)/1000) + ' seconds.';
              outputs.ecc_command_sysid = ecc_sysid;
         }  
  }
  else {
    // Async run: return sys_id of Commnad and exit.
    outputs.result = 'Async run.';
    outputs.ecc_command_sysid = ecc_sysid;
  }


})(inputs, outputs);

